import React, { Component } from "react";
import { connect } from "react-redux";
import "../../node_modules/video-react/dist/video-react.css"; // import css
import { Player, BigPlayButton } from "video-react";
import { getMovie } from "../actions";
import { Row, Col } from "antd";
import { Typography } from "antd";

const { Title } = Typography;
class Watch extends Component {
  componentWillMount() {
    const { params } = this.props.match;

    this.props.dispatch(getMovie(params.id));
  }

  render() {
    const { movie } = this.props;
    const { backdrop_path } = movie;
    console.log("movie", movie);

    return (
      <div>
        <div
          className="watch-header"
          style={{
            background:
              backdrop_path === null
                ? "#eee"
                : `url("https://image.tmdb.org/t/p/w342${backdrop_path}")`
          }}
        >
          <Player src="https://media.w3.org/2010/05/sintel/trailer_hd.mp4">
            <BigPlayButton position="center" />
          </Player>

          <Row type="flex" justify="center" className="watch-container">
            <Col span={5}>
              <Title level={3}>h3. Ant Design</Title>
            </Col>
          </Row>
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  error: state.moviesReducer.error,
  movies: state.moviesReducer.movies,
  pending: state.moviesReducer.pending,
  movie: state.getMovieReducer.movie
});

export default connect(mapStateToProps)(Watch);
